<?php endwhile; endif; ?><script type="text/javascript">jQuery('body').on("click", ".cahnge_email", function(e){	var new_email = jQuery('.new_email').val();		jQuery.ajax({		type: "post",		dataType: "json",		url : "<?php echo admin_url('admin-ajax.php'); ?>",		//data: email,		data: {action:'get_data', new_email:new_email},		success: function(msg){			if(msg.success == true){				jQuery('.message').removeClass('error');					jQuery('.message').addClass('success');					jQuery('.message.success').html(msg.data);			}else{				jQuery('.message').removeClass('success');				jQuery('.message').addClass('error');					jQuery('.message.error').html(msg.data);			}		}	});});</script>
